    var uploadFileList = [];
    var tmpNamePrefix = 'Uploading Image';
    var tmpURLPrefix = 'Reference.of.Image';
    var nextUptoken = false;
    var upTokenTimer = false;

    function setWorkDom( dom) {
        currentDom = dom;
        checkBrowser();
        if( broswerGroup === 1 ) {
            //return false;
        }
        currentDom.addEventListener('focus', startWork);
        currentDom.addEventListener('blur', stopWork);
        if( broswerGroup) {
            checkAppendDom();
        }
    }

    function checkBrowser() {
        if( navigator.userAgent.toLowerCase().match('firefox')) {
            broswerGroup = 2; // not so bad
        };
        if( navigator.userAgent.toLowerCase().match('msie')) {
            broswerGroup = 2; // not so bad
        };
        if( navigator.userAgent.toLowerCase().match('safari')) {
            broswerGroup = 1; // i hate safari
        };
        if( navigator.userAgent.toLowerCase().match('chrome')) {
            broswerGroup = 0; // nice browser
        };
        if( navigator.userAgent.toLowerCase().match('opera')) {
            broswerGroup = 0; // nice browser
        };
    }

    function checkAppendDom() {
        divDom = currentDom.prevSibling;
        if ( divDom && divDom.tagName && divDom.tagName.toLowerCase() === 'div' && divDom.id.macth('clipboardreader-')) {
        } else {
            divDom = document.createElement('div');
            divDom.id = 'clipboardreader-' + new Date().getTime();
            divDom.setAttribute('style', 'width:0px;height:0px;opacity:0');
            divDom.setAttribute('contentEditable', 'true');
            currentDom.parentNode.insertBefore( divDom, currentDom);
        }
    }

    function startWork ( e) {
        if( currentDom != e.currentTarget) {
            currentDom = e.currentTarget;
        }
        if (broswerGroup > 1) {
	        e.target.addEventListener( 'keydown', getPasteAction);
            e.target.parentNode.addEventListener( 'dragover', setDragOption);
        } else {
            e.target.addEventListener( 'paste', getPasteAction);
            e.target.parentNode.addEventListener( 'drop', getDragAction);
            e.target.parentNode.addEventListener( 'dragover', setDragOption);
        }

        if(!nextUptoken) {
            getUptoken();
        }
    }

    function stopWork (e) {
        if (broswerGroup > 1) {
            e.target.removeEventListener( 'keydown', getPasteAction);
        } else {
            e.target.removeEventListener( 'paste', getPasteAction);
            e.target.parentNode.removeEventListener( 'drop', getDragAction);
        }
    }

    function setDragOption(e) {
        e.preventDefault();
        var dataTransfer = e.originalEvent.dataTransfer;
        if(dataTransfer && dataTransfer.dropEffect) {
            dataTransfer.dropEffect = "copy";
        }
    }

    function getDragAction(e) {
        e.preventDefault();
        if (broswerGroup === 0) {
            var items = e.dataTransfer.items;
            if ( !items) {
                return;
            }
            var item = items[0];
            var textitem = items[0];
            types = e.dataTransfer.types || [];
            for (var i = 0; i < types.length; i++){
                if(types[i] === 'Files') {
                    item = items[i];
                    break;
                }
            }
            if( item && item.kind === 'file' && item.type.match(/^image\//i) ) {
                e.preventDefault();
                readImageFromDataTransferItem(item);
            }
        } else if (broswerGroup === 1) {
            var file = e.dataTransfer.files[0];
            if (file.type.toLowerCase().match('image')) {
                e.preventDefault();
                var reader = new FileReader();
                reader.onload = function(e){
                    var dataURL = e.target.result;
                    if(dataURL.toLowerCase().match('image')) {
                        dealWithData(dataURL);
                    }
                };
                reader.readAsDataURL(file);
            }
        }
    }

    function getPasteAction (e) {
        saveRange();
        if (broswerGroup > 1) {
            var controlBtn = false;
            if (e.keyCode === 17 ||  e.keyCode === 224) {
                controlBtn = true;
            }
            if ((e.ctrlKey != null) && (e.metaKey != null)) {
                controlBtn = e.ctrlKey || e.metaKey;
            }
            if (controlBtn && e.keyCode === 86) {
                divDom.focus();
                setTimeout(focusCurrentDom, 10);
            }
        } else {
            var clipboardData = e.clipboardData;
            if(broswerGroup == 1) {
                var items = clipboardData.files;
                if( items[0] && items[0].type.match(/^image\//i) ) {
                    e.preventDefault();
                    var reader = new FileReader();
                    reader.onload = function(e){
                        var dataURL = e.target.result;
                        if(dataURL.match('image')) {
                            dealWithData(dataURL);
                        }
                    };
                    reader.readAsDataURL(items[0]);
                }
            } else {
                items = clipboardData.items;
                if (!items) {
                    return;
                }
                var item = items[0];
                var textitem = items[0];
                types = clipboardData.types || [];
                for ( var i = 0; i < types.length; i++ ){
                    if( types[i] === 'Files' ) {
                        item = items[i];
                        break;
                    }
                }
                if( item && item.kind === 'file' && item.type.match(/^image\//i) ) {
                    e.preventDefault();
                    readImageFromDataTransferItem(item);
                }
            }
        }
    };

    function saveRange() {
        if ( document.selection) {
            IERange = document.selection.createRange();
        } else if (typeof currentDom.selectionStart === 'number' && typeof currentDom.selectionEnd === 'number') {
            W3CRange = {
                startPos: currentDom.selectionStart,
                endPos: currentDom.selectionEnd,
                cursorPos: currentDom.selectionStart
            }
        } else {
            // do nothing
        }
    }

    function focusCurrentDom() {
        currentDom.focus();
        var imageData = divDom.getElementsByTagName('img')[0] && divDom.getElementsByTagName('img')[0].getAttribute('src');
        if( imageData && imageData.indexOf('data:image') === 0) {
            dealWithData( imageData);
            divDom.innerHTML = '';
        } else {
            pasteData( divDom.innerText, '');
            divDom.innerHTML = '';
        }
    };

    function readImageFromDataTransferItem ( item) {
        var blob = item.getAsFile(),
            reader = new FileReader();
        reader.onload = function( e){
            var dataURL = e.target.result;
            if( dataURL.toLowerCase().match('image')) {
                dealWithData( dataURL);
            }
        };
        reader.readAsDataURL( blob);
    };

