var webNotificationService = (function(){
    var enabled = false;
    var subscribed = false;
    var browserGroup = 0;
    var service = null;
    var subscription = null;

    function init() {
        checkSystem();
        registerService();
    }

    function checkBrowser() {
        if( navigator.userAgent.toLowerCase().match('firefox')) {
            browserGroup = 1;
        };
        if( navigator.userAgent.toLowerCase().match('msie')) {
            browserGroup = 0;
        };
        if( navigator.userAgent.toLowerCase().match('safari')) {
            browserGroup = 2;
        };
        if( navigator.userAgent.toLowerCase().match('chrome')) {
            browserGroup = 1;
        };
        if( navigator.userAgent.toLowerCase().match('opera')) {
            browserGroup = 0;
        };
    }

    function checkSystem() {
        checkBrowser();
    }

    function registerService() {
        if( browserGroup === 1) {
            if ('serviceWorker' in navigator) {
                navigator.serviceWorker.register('serviceWorker.js').then(function() {
                    return navigator.serviceWorker.ready;
                }).then(function(serviceWorkerRegistration) {
                    service = serviceWorkerRegistration;
                    enabled = true;
                }).catch(function(error) {
                    console.error('Service Worker Error: ', error);
                    enabled = false;
                    browserGroup = 0;
                });
            } else {
                enabled = false;
                browserGroup = 0;
            }
        } else if ( browserGroup === 2) {
            if ('safari' in window && 'pushNotification' in window.safari) {
                var permissionData = window.safari.pushNotification.permission('web.com.tracup');
                service = permissionData;
                enabled = true;
            } else {
                enabled = false;
                browserGroup = 0;
            }
        } else {
            enabled = false;
            browserGroup = 0;
        }
    }

    function subscribe(callbackService) {
        if( browserGroup === 1) {
            service.pushManager.subscribe({userVisibleOnly: true}).
            then(function(pushSubscription) {
                subscription = pushSubscription;
                setEndpointData( JSON.stringify(pushSubscription));
                subscribed = true;
            }).catch(function(error) {
                //console.log('Error in subscribing', error);
                webNotificationService.onSubscribeError({ type:'permission'});
            });
        } else if ( browserGroup === 2) {
            if( callbackService) {
                service = callbackService;
            }
            //console.log(service);
            if (service.permission === 'default') {
                // This is a new web service URL and its validity is unknown.
                window.safari.pushNotification.requestPermission(
                    'https://www.tracup.com/webnotification/',// The web service URL.
                    'web.com.tracup',// The Website Push ID.
                    { uKey: 'bug.pgyer.com.web.notification.user'},// Data that you choose to send to your server to help you identify the user.
                    subscribe// The callback function.
                );
            }
            else if (service.permission === 'denied') {
                // The user said no.
                subscribed = false;
                webNotificationService.onSubscribeError({ type:'permission'});
            }
            else if (service.permission === 'granted') {
                // The web service URL is a valid push provider, and the user said yes.
                // permissionData.deviceToken is now available to use.
                subscribed = true;
                //console.log( service.deviceToken);
                setEndpointData( '{ "deviceToken": "'+service.deviceToken+'"}');
                //service.deviceToken;
            }
        }
    }

    function unSubscribe() {
        if( browserGroup === 1) {
            subscription.unsubscribe().then(function(event) {
                subscribed = false;
            }).catch(function(error) {
                //console.log('Error in unsubscribing', error);
                webNotificationService.onSubscribeError({ type:'permission'});
            });
        } else if ( browserGroup === 2) {

        }
    }

    function getServiceStatus() {
        return  enabled;
    }

    function getSubscribeStatus() {
        return subscribed;
    }

    function setEndpointData( data) {
        //console.log( data);
        var xhr = new XMLHttpRequest();
        xhr.onreadystatechange = function (){
            if( xhr.readyState == 4 ) {
                var response = xhr.responseText;
                if( response) {
                    webNotificationService.onSubscribe(response);
                } else {
                    //console.log('we got error when save subscription data');
                    webNotificationService.onSubscribeError({ type:'network'});
                }
            }
        }
        xhr.open("post", '/api/plugin/refreshEndPoint/', true);
        xhr.setRequestHeader('Content-Type','application/x-www-form-urlencoded');
        xhr.send('data=' + encodeURIComponent(data) + '&type=endpoint');
    }

    function onSubscribe ( data) {};
    function onSubscribeError ( data) {};

    init();

    return {
        subscribe: subscribe,
        onSubscribe: onSubscribe,
        onSubscribeError: onSubscribeError,
        unSubscribe: unSubscribe,
        getServiceStatus: getServiceStatus,
        getSubscribeStatus: getSubscribeStatus
    }
})();

